perspectives.style:
@settings {
  template: stakeholder;
  theme: dark;
  layout: static;
}

element {
  image-size: contain;
  image-visibility: visible;
  outline-offset: 0;
  shadow-opacity: 0;
  font-size: 30;
  font-family: Oswald;
  shadow-color: white;
  text-overflow: wrap 7;
}

