faction {
  border-width: 10;
  size: 200;
  font-size: 100;
  text-align: center;
  font-weight: bold;
  shadow-opacity: 0.1;
  shadow-size: 5;
  label-visibility: hidden;
}

coalition {
  bullseye-color: transparent;
  bullseye-size: 1;
  color: transparent;
  image-visibility: none;
  size: 100;
  border-width: 10;
  font-size: 70;
  text-align: center;
  text-overflow: wrap 1;
}

element["Titles"*="Autarkis"] {
  size: 100;
}

