element["StatusNum"=5], element["Titles"*="Autarkis"] {
  size: 100;
  font-size: 60;
  text-align: center;
  font-family: candal;
  text-overflow: wrap 2;
}

connection {
  arrow-visibility: hidden;
  curvature: 0;
  display: none;
}

connection.boss {
  display: map;
  border-width: 2;
  strength: 4;
  length: 2;
  size: 15;
  border-opacity: 1;
  border-color: white;
  arrow-visibility: visible;
  arrow-color: white;
  arrow-width: 6;
  arrow-height: 6;
}

element.pc {
  size: 70;
  font-size: 45;
}

element.dead {
  opacity: 0.3;
  font-color: grey;
  color: grey;
}

:to(coalition) {
  display: map;
  strength: 2;
  length: 150;
}

:from(coalition):to(faction) {
  display: map;
  strength: 5;
  length: 50;
  size: 50;
}

connection["Connection Type"="Sire/Regnant"] {
  display: map;
  strength: 0;
  length: 200;
  style: dashed;
  size: 4;
  arrow-visibility: visible;
  arrow-width: 4;
  arrow-height: 4;
  opacity: 1;
  curvature: 0;
}

:from(kindred:not(kindred <--> coalition) <--> faction) {
  display: map;
}
