perspectives.style:
@controls {
}

@settings {
  template: stakeholder;
  theme: dark;
}

element {
  image-size: contain;
  image-visibility: visible;
  outline-offset: 0;
  shadow-opacity: 0;
  font-size: 30;
  font-family: Voltaire;
  shadow-color: white;
}

element {
  popover: "{{label}}";
}

element["Quote"] {
  popover: "<blockquote>{{quote}}</blockquote>";
}

element["Description"] {
  popover: "{{description}}";
}

element["Quote"]element["Description"] {
  popover: "<blockquote>{{quote}}</blockquote>{{description}}";
}

element["Image"] {
  popover: "{{image}}";
  popover-width: 800;
}

faction {
  border-width: 10;
  size: 200;
  font-size: 100;
  text-align: center;
  font-weight: bold;
  shadow-opacity: 0.1;
  shadow-size: 5;
  label-visibility: hidden;
}

coalition {
  bullseye-color: transparent;
  bullseye-size: 1;
  color: transparent;
  image-visibility: none;
  size: 100;
  border-width: 10;
  font-size: 70;
  text-align: center;
}

element["Titles"*="Autarkis"] {
  size: 100;
}

