connection {
  arrow-visibility: hidden;
}

connection.boss {
  border-width: 2;
  strength: 4;
  length: 2;
  border-opacity: 1;
  border-color: white;
  arrow-visibility: visible;
  arrow-color: white;
  arrow-width: 6;
  arrow-height: 6;
}

element.pc {
  size: 70;
  font-size: 45;
}

element.dead {
  opacity: 0.3;
  font-color: grey;
  color: grey;
}

:from(kindred):to(coalition) {
  display: map;
  strength: 2;
  length: 150;
}

:from(coalition):to(faction) {
  display: map;
  strength: 5;
  length: 50;
}

sire/regnant {
  display: map;
  strength: 0;
  length: 200;
  style: dashed;
  size: 8;
  arrow-visibility: visible;
  arrow-width: 4;
  arrow-height: 4;
}
